using System;using System.Collections.Generic;namespace CrimeSolver{class Program{static void Main(string[] args){s();Console.ReadLine();}static void s(){bool[] t=new bool[]{true,false};foreach(bool a in t){foreach(bool b in t){foreach(bool c in t){foreach(bool d in t){if(f(!d,!b)&f(!c,d)&f(a,!d)&f(c,!a)){Console.WriteLine(w(a,b,c,d));}}}}}}static bool f(bool x,bool y){return((!x&&y)||(x&&!y));}static string w(bool a,bool b,bool c,bool d){IList<string> n=new List<string>();if(a)n.Add("Alex");if(b)n.Add("Brad");if(c)n.Add("Charlie");if(d)n.Add("David");string v=string.Join(", ",n);return"The crime was commited by: "+v+".";}}}
